import React from 'react';
import { MDBProgress } from 'mdbreact';
import '../App.css';
import { Row, Col } from 'react-bootstrap';
import { FacebookIcon, FacebookShareButton, WhatsappShareButton, WhatsappIcon, TwitterShareButton, TwitterIcon } from 'react-share';

const Progressbar = (props) => {
  return (
    <div>
      {props.house.map(house => {
        var goal = 50;
        var current = 50;
        var percent = goal - current;
        var progressPercent = 100 - percent;
        const code = house.dwelling_code;
        const HE = true;
        const SM = false;

        function money() {
          if (current !== goal) {
            if (HE === true) {
              return (
                <span>Your Goal: {goal}kWh</span>
              );
            } else if (SM === true) {
              return (
                <span>Your Goal: £{goal}</span>
              );
            }
          } else if (current === goal) {
            if (HE === true) {
              return (
                <React.Fragment>
                  <span>GOAL!</span>
                  <FacebookShareButton url={"https://flonne.me/"} quote={`I just reached my goal of saving ${goal}kWh of energy with Öko!`} className="share">
                    <FacebookIcon size={32} round={true}></FacebookIcon>
                  </FacebookShareButton>
                  <WhatsappShareButton url={"https://flonne.me/"} title={`I just reached my goal of saving ${goal}kWh of energy with Öko!`} className="share">
                    <WhatsappIcon size={32} round={true}></WhatsappIcon>
                  </WhatsappShareButton>
                  <TwitterShareButton url={"https://flonne.me/"} title={`I just reached my goal of saving ${goal}kWh of energy with Öko!`} hashtags={okosmart} className="share">
                    <TwitterIcon size={32} round={true}></TwitterIcon>
                  </TwitterShareButton>
                </React.Fragment>
              );
            } else if (SM === true) {
              return (
                <React.Fragment>
                  <span>GOAL!</span>
                  <FacebookShareButton url={"https://flonne.me/"} quote={`Just reached my goal of saving £${goal} with Öko!`} className="share">
                    <FacebookIcon size={32} round={true} />
                  </FacebookShareButton>
                </React.Fragment>
              );
            }
          }
        }

        if (code === "XYZ-ABC") {
          if (HE === true) {
            return (
              <React.Fragment>

                <Row>
                  <Col sm="10">
                    <MDBProgress material striped value={progressPercent} animated color="default">
                      <span style={{ fontSize: '1.25em' }}>Percentage to Goal: {percent}%</span>
                    </MDBProgress>
                  </Col>
                  <Col sm="2">
                    <div>{money()}</div>
                  </Col>
                </Row>

              </React.Fragment>
            );
          }


          if (SM === true) {
            return (
              <React.Fragment>

                <Row>
                  <Col sm="10">
                    <MDBProgress material striped value={progressPercent} animated color="default">
                      <span style={{ fontSize: '1.25em' }}>Percentage to Goal: {percent}%</span>
                    </MDBProgress>
                  </Col>
                  <Col sm="2">
                    <div>{money()}</div>
                  </Col>
                </Row>

              </React.Fragment>
            );
          }

        }
      })}
    </div>
  )
};

export default Progressbar;


