import React, { Component } from 'react';
import './App.css';
import Header from './components/header';
import Sidebar from './components/sidebar';
import Progressbar from './components/progressbar';
import Footer from './components/footer'
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap-css-only/css/bootstrap.min.css';
import 'mdbreact/dist/css/mdb.css';
import ChartsPage from './components/ChartsPage';
import RightSidebar from './components/right-sidebar';
//import Leaderboard from './components/leaderboard';
//import {withCookies} from 'react-cookie';
import { Line, Pie } from 'react-chartjs-2';
import { MDBContainer } from 'mdbreact';
import SectionContainer from './components/sectionContainer';
import LineGraph from './components/lineGraph';



class App extends Component {

  state = {
    device: [],
    userAccount: [],
    divColor: "white",
    buttonColor: "purple"
  }

  componentDidMount() {
    fetch('https://oko-api.herokuapp.com/dwelling/device/', {
      method: 'GET',
      headers: {
        //'Authorization': 'Token 53aaf969d1e6ee660f11a9cb99da97338232d86e'
      }
    }).then(resp => resp.json())
      .then(resp => this.setState({ device: resp }))
      .catch(error => console.log(error))

    fetch('http://oko-api.herokuapp.com/account/users/', {
      method: 'GET',
      headers: {
        //'Authorization': 'Token 53aaf969d1e6ee660f11a9cb99da97338232d86e'
      }
    }).then(resp => resp.json())
      .then(resp => this.setState({ userAccount: resp }))
      .catch(error => console.log(error))
  }

  deviceClicked = d => {
    console.log(d)
  }


  userAccountClicked = a => {
    console.log(a)
  }

  handleButtonClick = () => {
    this.setState({
      divColor: "#FBF9B7",
      buttonColor: "blue"
    })
  }


  render() {
    return (
      <div>

        <div style={{ background: this.state.divColor }}>

          <div className="grid">
            <header>
              <Header></Header>
            </header>

            <aside className="sidebar-left">
              <Sidebar></Sidebar>
            </aside>

            <article>
              <Progressbar userAccount={this.state.userAccount} userAccountClicked={this.a}></Progressbar>

              <div>
                <MDBContainer>

                  <div className="row">

                    <div className="col-md-6">

                      <h3 style={{ color: 'black' }}>Used so far today: 51 kWh</h3>
                      <SectionContainer>
                        <LineGraph></LineGraph>
                      </SectionContainer>
                    </div>
                    <div className="col-md-6">
                      <h3 style={{ color: 'black' }}>Devices used today</h3>
                      <SectionContainer>
                      <ChartsPage device={this.state.device} deviceClicked={this.d}></ChartsPage>
                      </SectionContainer>
                    </div>
                  </div>
                </MDBContainer>
              </div>

              <br></br>
            </article>

            <aside>
              <RightSidebar></RightSidebar>
            </aside>

            <footer>
              <Footer></Footer>
              <button style={{ background: this.state.buttonColor, width: '30em', fontSize: '0.85em' }} onClick={this.handleButtonClick}>
                Change background colours for accessibility.
              </button>
            </footer>
          </div>
        </div>

      </div>
    );
  }
}

export default App;
