import React, { Component } from 'react';
import './App.css';
import Header from './components/header';
import Sidebar from './components/sidebar';
import Footer from './components/footer';
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap-css-only/css/bootstrap.min.css';
import 'mdbreact/dist/css/mdb.css';
import MovieList from './components/movie-list'
import RightSidebar from './components/right-sidebar';


class Rooms extends Component {

  state = {
    room: [],
    selectedMovie: null
  }

  componentDidMount() {
    fetch('https://oko-api.herokuapp.com/dwelling/room/', {
      method: 'GET',
      headers: {
        //'Authorization': 'Token 53aaf969d1e6ee660f11a9cb99da97338232d86e'
      }
    }).then(resp => resp.json())
      .then(resp => this.setState({room: resp}))
      .catch(error => console.log(error))
  }

  movieClicked = movie => {
    console.log(movie)
  }





  constructor(props) {
    super(props);
    const room = {};

    this.state = {
      room
    }
  }

  componentDidMount() {
    Node.getRootNode(this.props.room_id).then((result) => {
      console.log(result.data);
      this.setState(() => ({room: result.data}));
    }).catch(function(error) {
      // TODO: handle error
    });
  }




  render() {

    if (!this.state.room.device) {
      return null;
  }



    return (
      <React.Fragment>
        <div class="grid">
          <header>
            <Header></Header>
          </header>
          <aside class="sidebar-left">
            <Sidebar></Sidebar>
          </aside>
          <article>
            <MovieList room={this.state.room} movieClicked={this.room}/>





      <div>
        {this.state.room.device.id}
      </div>


          </article>
          <aside class="sidebar-right">
            <RightSidebar></RightSidebar>
          </aside>

          <footer>
            <Footer></Footer>
          </footer>
        </div>



      </React.Fragment>
    );
  }
}

export default Rooms;
