import React from 'react';

class DevicesMap2 extends component {
    componentWillMount(){
        const searchResults = FBAppDB.ref('rooms');
        const location = this.props.location;
        const instrument = this.props.instrument;

        searchResults.orderByChild('location').equalTo(location).on('value', (snap) => {
            const rooms = snap.val();
            const profiles = Object.keys(rooms).map((key) => rooms[key]);

            // previously we were setting just profiles[0]; but I think you need 
            // the entire list of rooms as an array to render as search results
            this.setState({ rooms: profiles }); // set the whole data to state
        });
    }


    render(){
        const rooms = this.state.rooms || []; // note that this.state.rooms is an array

        return(
            <Row>
                {
                rooms.length > 0 
                ? rooms.map((room_data) => <SearchResult room_data={room_data} />)
                : null
                }
            </Row>
        );
    }
}

export default DevicesMap2;
