import React from 'react';
import { MDBProgress } from 'mdbreact';
import '../App.css';
import { Row, Col} from 'react-bootstrap';


const Progressbar = (props) => {
  return (
    <div>
      {props.house.map(house => {
        var goal = 50;
        var current = 30;
        var percent = goal - current;
        const code = "XJE2-LHA";
        const HE = true;
        const SM = false;

        function money () {
          if(HE === true){
            return(
              <span>Your Goal: {goal}kWh</span>
            );
          } else if (SM === true){
            return(
              <span>Your Goal: £{goal}</span>
            );
          }
        }

        if (code === "XJE2-LHA") {
          if(HE === true){
            return (
              <React.Fragment>

              <Row>
                <Col sm="10">
                    <MDBProgress material striped value={goal} animated color="default">
                      <span style={{fontSize: '1.25em'}}>Percentage to Goal: {percent}%</span>
                    </MDBProgress>
                </Col>
                <Col sm="2">
                  <div>{money()}</div>
                </Col>
              </Row>

            </React.Fragment>
            );
          }


          if(SM === true){
            return (
              <React.Fragment>

              <Row>
                <Col sm="10">
                    <MDBProgress material striped value={goal} animated color="default">
                      <span style={{fontSize: '1.25em'}}>Percentage to Goal: {percent}%</span>
                    </MDBProgress>
                </Col>
                <Col sm="2">
                  <p>Your Goal: £{goal}</p>
                </Col>
              </Row>

            </React.Fragment>
            );
          }

        }
      })}
    </div>
  )
};

export default Progressbar;


