import React, { Component } from 'react';
import './App.css';
import Header from './components/header';
import Sidebar from './components/sidebar';
import 'bootstrap/dist/css/bootstrap.min.css';
import Card from 'react-bootstrap/Card';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap-css-only/css/bootstrap.min.css';
import 'mdbreact/dist/css/mdb.css';
import Footer from './components/footer';
import PersonAddTwoToneIcon from '@material-ui/icons/PersonAddTwoTone';
import PeopleAltTwoToneIcon from '@material-ui/icons/PeopleAltTwoTone';
import PersonOutlineTwoToneIcon from '@material-ui/icons/PersonOutlineTwoTone';
import RightSidebar from './components/right-sidebar';
import { CSVLink } from "react-csv";
import PersonalInfo from './components/personalInfo';
import DwellingInfo from './components/dwellingInfo';


class Settings extends Component {

  state = {
    userAccount: [],
    selectedMovie: null,
    credentials: {
      goal: ''
    }
  }

  componentDidMount() {
    fetch('http://oko-api.herokuapp.com/account/users/?fbclid=IwAR0zRxvYYfFYwVZjkukFkWu9GF7BGUACPzgG_40Nd39VFuoegLJdVIWvDMU', {
      method: 'GET',
      headers: {
        //'Authorization': 'Token 53aaf969d1e6ee660f11a9cb99da97338232d86e'
      }
    }).then(resp => resp.json())
      .then(resp => this.setState({ userAccount: resp }))
      .catch(error => console.log(error))
  }

  accountClicked = a => {
    console.log(a)
  }

  inputChanged = event => {
    let cred = this.state.credentials;
    cred[event.target.name] = event.target.value;
    this.setState({ credentials: cred });
  }

  setGoal = event => {
    console.log(this.state.credentials);
    fetch('http://oko-api.herokuapp.com/account/users/?fbclid=IwAR0zRxvYYfFYwVZjkukFkWu9GF7BGUACPzgG_40Nd39VFuoegLJdVIWvDMU', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(this.state.credentials)
    }).then(res => {
      console.log(res.token);
      this.props.cookies.set('token', res.token);
    })
      .catch(error => console.log(error))
  }


  render() {

    return (
      <div>
        {this.state.house.map(house => {

          const name = house.dwelling_name;
          const username = ["gCol"];
          const DOB = ["02/04/1999"];
          const address = ["123 Fake street"];
          const tel = ["075166284064"];
          const email = ["gemmaC@gmail.com"];
          const password = ["123Gemma30"];
          const code = house.dwelling_code;
          const incent_choice = "HE";

          const csvData = [
            [name],
            [username],
            [DOB],
            [address],
            [tel],
            [email],
            [password],
            [code]
          ];

          function setGoal() {
            if (incent_choice === 'HE') {
              return (
                <Card.Text>
                  Set your goal for how much energy you want to save in kWh
                </Card.Text>
              );

            } else if (incent_choice === 'SM') {
              return (
                <Card.Text>
                  Set your goal for how much money you want to save in Â£
                </Card.Text>
              );
            }
          }

          if (code === "ABC-XYZ") {

            return (

              <div>

                <div class="grid">
                  <header>
                    <Header></Header>
                  </header>

                  <aside class="sidebar-left">
                    <Sidebar></Sidebar>
                  </aside>

                  <article>
                    <h1>Settings</h1>
                    <Card>
                      <Card.Body>
                        <Card.Title>Set Your Goal</Card.Title>
                        <Card.Subtitle className="mb-2 text-muted"></Card.Subtitle>
                        <div>{setGoal()}</div>
                        <hr></hr>
                        <tr>
                          <Card.Text class="setting-devices">
                            Please enter your goal:
                        </Card.Text>
                        </tr>
                        <tr>
                          <input type="text" name="goal" value={this.state.credentials.goal} onChange={this.inputChanged} /><br />
                          <button onClick={this.setGoal} className="dwellCode_button">
