import React, { Component } from 'react';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';
import Form from 'react-bootstrap/Form'


class AddRoom extends Component {
  state = {
    modal: false,
    credentials: {
      room_name: '',
      room_code: 'ABC-XYZ_4',
      related_dwelling: 'ABC-XYZ',
      data: [{
        related_room: 'ABC-XYZ_4',
        co2: '0',
        humidity: '0',
        temperature: '0'
      }],
      devices:[{
        device_code: 'ABC-XYZ_4_1',
        device_name: '',
        mac_address: '',
        energy_used: '0',
        state: false,
        room: 'ABC-XYZ_4'
      }]
    }
  }

  toggle = () => {
    this.setState({
      modal: !this.state.modal
    });
  }

  inputChanged = event => {
    let cred = this.state.credentials;
    cred[event.target.name] = event.target.value;
    this.setState({ credentials: cred });
  }

  registerRoom = event => {
    console.log(this.state.credentials);
    fetch('https://oko-api.herokuapp.com/dwelling/house/', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(
        this.state.credentials
    )
    }).then(res => {
      console.log(res.token);
    })
      .catch(error => console.log(error))
  }


  render(props) {

    return (
      <MDBContainer>
        <MDBBtn onClick={this.toggle}>Add a New Room</MDBBtn>
        <MDBModal isOpen={this.state.modal} toggle={this.toggle}>
          <MDBModalHeader toggle={this.toggle} style={{ color: 'black' }}>Add Room</MDBModalHeader>
          <MDBModalBody>
            <Form>
              <Form.Group controlId="exampleForm.ControlInput1">
                <Form.Label style={{ color: 'black' }}>Room Name</Form.Label>
                <input type="text" name="room_name" placeholder="Example device" value={this.state.credentials.room_name} onChange={this.inputChanged} />
                <Form.Label style={{ color: 'black' }}>Device Name</Form.Label>
                <input type="text" name="device_name" placeholder="Example device" value={this.state.credentials.devices.device_name} onChange={this.inputChanged} />
              </Form.Group>
            </Form>
          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn color="secondary" onClick={this.toggle}>Close</MDBBtn>
            <MDBBtn color="primary" onClick={this.registerRoom} >Save changes</MDBBtn>
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    );
  }
}

export default AddRoom;
