import React from 'react'
import Tabs from 'react-bootstrap/Tabs';
import Tab from 'react-bootstrap/Tab';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import Button from 'react-bootstrap/Button';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import ChartsPageRoom from './ChartsPageRooms';
import { Line } from 'react-chartjs-2';
import { MDBContainer } from 'mdbreact';
import SectionContainer from './sectionContainer';

function MovieList(props)  {

    // const movieClicked = movie => evt =>{
    //     props.movieClicked(movie)
    //  }


    return (

        <div>
            {props.room.map(room => {

                let dataSet = room.room_data[0].co2;
                var dataArray = dataSet.split(',', 10);
                var hello = 1;


                const dataLine = {
                    labels: ['00:00', '01:00', '02:00', '03:00', '04:00', '05:00', '06:00', '07:00', '08:00', '09:00', '10:00', '11:00', '12:00', '13:00', '14:00', '15:00', '16:00', '17:00', '18:00', '19:00', '20:00', '21:00', '22:00', '23:00'],
                    datasets: [
                      {
                        fill: false,
                        lineTension: 0.1,
                        backgroundColor: 'rgba(75,192,192,0.4)',
                        borderColor: 'rgba(75,192,192,1)',
                        borderCapStyle: 'butt',
                        borderDash: [],
                        borderDashOffset: 0.0,
                        borderJoinStyle: 'miter',
                        pointBorderColor: 'rgba(75,192,192,1)',
                        pointBackgroundColor: '#fff',
                        pointBorderWidth: 1,
                        pointHoverRadius: 5,
                        pointHoverBackgroundColor: 'rgba(75,192,192,1)',
                        pointHoverBorderColor: 'rgba(220,220,220,1)',
                        pointHoverBorderWidth: 2,
                        pointRadius: 1,
                        pointHitRadius: 10,
                        data: [{hello}]
                      }
                    ]
                  };

                return (

                    <React.Fragment>
                        <div key={room.room_data[0].co2}>{dataArray}</div>
                        <Card style={{ width: '30rem' }}>
                            <Card.Body>
                                <Card.Title key={room.name}>{room.room_name}</Card.Title>
                                <Card.Subtitle className="mb-2 text-muted">
                                </Card.Subtitle>
                                <Card.Text>
                                    <Tabs defaultActiveKey="data" transition={false} id="noanim-tab-example">
                                        <Tab eventKey="data" title="Data">
                                            <br></br>
                                            <MDBContainer>

                                                <div class="row">
                                                <div class="col-md-6">

                                                    <SectionContainer>
                                                    <Line data={dataLine} options={{ responsive: true }} />
                                                    </SectionContainer>
                                                </div>
                                                </div>



                                            </MDBContainer>
                                        </Tab>
                                        <Tab eventKey="Devices" title="Devices">
                                            <ListGroup>
                                                <br></br>
                                                <ListGroup.Item key={room.device_name}>{room.devices[0].device_name}</ListGroup.Item>
                                                <ListGroup.Item>
                                                    <ButtonGroup size="md">
                                                        <Button>View all devices</Button>
                                                        <Button>Add Device to Room</Button>
                                                    </ButtonGroup>
                                                </ListGroup.Item>
                                            </ListGroup>
                                        </Tab>
                                    </Tabs>
                                </Card.Text>
                            </Card.Body>
                        </Card>

                    </React.Fragment>
                )
            })}
        </div>

    )
}
export default MovieList;
