import React, { Component } from 'react';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';
import Form from 'react-bootstrap/Form'

function ReturnRoom(props) {
  return (

    <div>
      {props.room.map(room => {

        inputChanged = event => {
          let cred = this.state.credentials;
          cred[event.target.name] = event.target.value;
          this.setState({ credentials: cred });
        }

        login = event => {
          if (this.state.isLoginView) {
              console.log(this.state.credentials);
              fetch('oko-api.herokuapp.com/account/register', {
                  method: 'POST',
                  headers: { 'Content-Type': 'application/json' },
                  body: JSON.stringify(this.state.credentials)
              }).then(res => {
                  console.log(res.token);
                  this.props.cookies.set('token', res.token);
                  window.location.href = "/Oko/"
              })
                  .catch(error => console.log(error))
          }
        }

        return(
          <React.Fragment>

          </React.Fragment>
        );
      })}
    </div>
  );
}

class AddRoom extends Component {
  state = {
    modal: false
  }

  toggle = () => {
    this.setState({
      modal: !this.state.modal
    });
  }

  render() {
    return (
      <MDBContainer>
        <MDBBtn onClick={this.toggle}>Add a New Room</MDBBtn>
        <MDBModal isOpen={this.state.modal} toggle={this.toggle}>
          <MDBModalHeader toggle={this.toggle} style={{ color: 'black' }}>Add Room</MDBModalHeader>
          <MDBModalBody>
            <Form>
              <Form.Group controlId="exampleForm.ControlInput1">
                <Form.Label style={{ color: 'black' }}>Room Name</Form.Label>
                <Form.Control type="name" placeholder="Example device" />
              </Form.Group>
            </Form>
          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn color="secondary" onClick={this.toggle}>Close</MDBBtn>
            <MDBBtn color="primary">Save changes</MDBBtn>
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    );
  }
}

export default AddRoom;
