import React from 'react'
import Tabs from 'react-bootstrap/Tabs';
import Tab from 'react-bootstrap/Tab';
import Card from 'react-bootstrap/Card';
import ChartsPage from './ChartsPageRooms';
import ListGroup from 'react-bootstrap/ListGroup'
import Button from 'react-bootstrap/Button'
import ButtonGroup from 'react-bootstrap/ButtonGroup'

function MovieList(props) {

    // const movieClicked = movie => evt =>{
    //     props.movieClicked(movie)
    //  }


    return (

        <div>
            {props.room.map(room => {
                return (

                    <React.Fragment>
                        <Card style={{ width: '30rem' }}>
                            <Card.Body>
                                <Card.Title>{room.room_name}</Card.Title>
                                <Card.Subtitle className="mb-2 text-muted">Card Subtitle</Card.Subtitle>
                                <Card.Text>
                                    <Tabs defaultActiveKey="data" transition={false} id="noanim-tab-example">
                                        <Tab eventKey="data" title="Data">
                                            <ChartsPage />
                                        </Tab>
                                        <Tab eventKey="Devices" title="Settings">

                                            <div>{room.devices}</div>
                                            <ListGroup>                                                <ListGroup.Item>
                                                <ButtonGroup size="md">
                                                    <Button>View all devices</Button>
                                                    <Button>Add Device to Room</Button>
                                                </ButtonGroup>
                                            </ListGroup.Item>
                                            </ListGroup>
                                        </Tab>
                                    </Tabs>
                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </React.Fragment>
                )
            })}
        </div>

    )
}
export default MovieList;
