import React from 'react';
import { MDBProgress } from 'mdbreact';
import '../App.css';
import { Row, Col} from 'react-bootstrap';
import { FacebookShareButton } from "react-share";

const Progressbar = (props) => {
  return (
    <div>
      {props.house.map(house => {
        var goal = 50;
        var current = 50;
        var percent = goal - current;
        var progressPercent = 100 - percent;
        const code = "XJE2-LHA";
        const HE = true;
        const SM = false;

        function money () {
          if(current !== goal){
            if(HE === true){
              return(
                <span>Your Goal: {goal}kWh</span>
              );
            } else if (SM === true){
              return(
                <span>Your Goal: Â£{goal}</span>
              );
            }
          } else if (current === goal){
            return(
              <React.Fragment>
                <span>Congrats hunni</span>
                <FacebookShareButton size={32} round={true}></FacebookShareButton>
              </React.Fragment>

            )
          }
        }

        if (code === "XJE2-LHA") {
          if(HE === true){
            return (
              <React.Fragment>

              <Row>
                <Col sm="10">
                    <MDBProgress material striped value={progressPercent} animated color="default">
                      <span style={{fontSize: '1.25em'}}>Percentage to Goal: {percent}%</span>
                    </MDBProgress>
                </Col>
                <Col sm="2">
                  <div>{money()}</div>
                </Col>
              </Row>

            </React.Fragment>
            );
          }


          if(SM === true){
            return (
              <React.Fragment>

              <Row>
                <Col sm="10">
                    <MDBProgress material striped value={goal} animated color="default">
                      <span style={{fontSize: '1.25em'}}>Percentage to Goal: {percent}%</span>
                    </MDBProgress>
                </Col>
                <Col sm="2">
                  <p>Your Goal: Â£{goal}</p>
                </Col>
              </Row>

            </React.Fragment>
            );
          }

        }
      })}
    </div>
  )
};

export default Progressbar;


