import React, { Component } from 'react';
import { Button } from 'react-bootstrap';



class DeleteDevice extends Component {
    state = {
        device_code: this.props.dataFromParent,
        credentials: {
            device_code: '',
            device_name: '',
            mac_address: '',
            energy_used: '',
            state: false,
            room: ''
        }
    }

    componentDidMount() {
        fetch('https://oko-api.herokuapp.com/dwelling/device/', {
            method: 'GET',
            headers: {
                //'Authorization': 'Token 53aaf969d1e6ee660f11a9cb99da97338232d86e'
            }
        }).then(resp => resp.json())
            .then(resp => this.setState({ device: resp }))
            .catch(error => console.log(error))
    }

    deleteDevice(event) {
        fetch(`https://oko-api.herokuapp.com/dwelling/device/ABC-XYZ_1_1/`, {
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                credentials: this.state.credentials
            })
        }).then((response) => response.json())
            .then((responseJson) => {
                this.setState({
                    showUsers: false,
                    users: responseJson
                });
                return;
            })
            .catch((error) => {
                throw (error);
            });
        event.preventDefault();
    }

    render() {
        return (
            <div>
                <Button onClick={this.deleteDevice}>Delete</Button>
                <p>{this.state.device_code}</p>
            </div>
        );
    }
}

export default DeleteDevice;
