                            <h3>Non-Admin Privileges</h3>
                        }
                        else if (this.state.isSuperAdminView === true){
                            <h3>Super-Admin Privileges</h3>
                        }
                    }

                    return(
                        <div>

                        </div>
                    );


                })}
            </div>
        );

    }
}

export default AdminPriv;
