import React, { Component } from 'react'
import axios from 'axios';
import { Line } from 'react-chartjs-2';

export class Linecharts extends Component {

    constructor(props) {

        super(props);

        this.state = { Data: {} };

    }

    componentDidMount() {

        axios.get(`https://oko-api.herokuapp.com/dwelling/room/`)

            .then(res => {

                console.log(res);

                const data = res.data;

                let co2 = [];

                let humidity = [];

                data.forEach(record => {

                    co2.push(record.co2);

                    humidity.push(record.humidity);

                });

                this.setState({

                    Data: {

                        labels: co2,

                        datasets: [

                            {

                                label: 'IPL 2018/2019 Top Run Scorer',

                                data: co2,

                                backgroundColor: [

                                    "#3cb371",

                                    "#0000FF",

                                    "#9966FF",

                                    "#4C4CFF",

                                    "#00FFFF",

                                    "#f990a7",

                                    "#aad2ed",

                                    "#FF00FF",

                                    "Blue",
                                    "Red"

                                ]

                            }

                        ]

                    }

                });

            })

    }

    render() {

        return (

            <div>

                <Line

                    data={this.state.Data}

                    options={{ maintainAspectRatio: false }} />

            </div>

        )

    }

}



export default Linecharts;
