import React, { Component } from 'react';
import '../index.css';
import Tabs from 'react-bootstrap/Tabs';
import Tab from 'react-bootstrap/Tab';
import RegisterDwelling from './RegisterDwelling';
import { withCookies } from 'react-cookie';
import { MDBContainer, MDBCollapse } from 'mdbreact';

class Login extends Component {

    state = {
        credentials: {
            goal: ''
        }
    }

    inputChanged = event => {
        let cred = this.state.credentials;
        cred[event.target.name] = event.target.value;
        this.setState({ credentials: cred });
    }

    login = event => {
        var elem = document.getElementById("goal");
        if (elem.value=="Close Curtain") elem.value = "Open Curtain";
        else elem.value = "Close Curtain";
    }

    render() {
        const { collapseID } = this.state;
        return (

            <React.Fragment>
                <input type="text" name="goal" value={this.state.credentials.goal} onChange={this.inputChanged} /><br />
                <button onClick={this.login}>
                    Submit
                </button>
                <p id="goal"></p>
            </React.Fragment>
        );
    }
}

export default Login;
