import React from 'react';

class ClockFunction extends React.Component {

        state = { 
            time: new Date(),
            isAnalogueView: true,
            isDigitalView: false

        }



    componentDidMount() { // create the interval once component is mounted
        this.update = setInterval(() => {
            this.setState({ time: new Date() });
        }, 1 * 1000); // every 1 seconds
    }

    componentWillUnmount() { // delete the interval just before component is removed
        clearInterval(this.update);
    }



    render() {

        function onClick() {
            var isAnalogueView = true;
            var isDigitalView = false;
            var time = new Date();

            if (isAnalogueView === true) {
                return (
                    <div>
                        <div>
                            {/* print the string prettily */}
                            {time.toLocaleTimeString()}
                        </div>
                    </div>
                );
            } else if(isDigitalView === true){
                return (
                    <React.Fragment>
                        <div className={"clock"}>
                            <h3>Clock</h3>
                            <div className={"analog-clock"}>
                                <div className={"dial seconds"} />
                                <div className={"dial minutes"} />
                                <div className={"dial hours"} />
                            </div>
                            <div className={"digital-clock"}></div>
                        </div>
                    </React.Fragment>
                );

            }
        }

        return (
            <div>{onClick()}</div>
        );
    }
}

export default ClockFunction;
