import React from 'react';

class devicesMap extends React.Component {
    constructor(props) {
      super(props);
      const node = {};

      this.state = {
        node
      }
    }

    componentDidMount() {
      Node.getNode(this.props.node_id).then((result) => {
        console.log(result.data);
        this.setState(() => ({node: result.data}));
      }).catch(function(error) {
        // TODO: handle error
      });
    }

    componentDidMount() {
        fetch('https://oko-api.herokuapp.com/dwelling/room/', {
          method: 'GET',
          headers: {
            //'Authorization': 'Token 53aaf969d1e6ee660f11a9cb99da97338232d86e'
          }
        }).then(resp => resp.json())
          .then(resp => this.setState({room: resp}))
          .catch(error => console.log(error))
      }


      render() {
        if (!this.state.room.device.device_name) {
          return null;
        }

        return (
          <div>
            {this.state.node.node_status.name}
          </div>
        );
      }
  };

  export default devicesMap;
