import React from 'react';
import { MDBBtn, MDBBtnGroup } from 'mdbreact';

const INCENT_VIEW_MODES = { TEXT: 'Todays Usage', VALUE: 'Usage' };

class IncentOnClick extends React.Component {
    state = { viewMode: INCENT_VIEW_MODES.TEXT };

    componentDidMount() {
        this.intervalId = setInterval(() => {
            this.setState({
                time: new Date()
            });
        }, 1000);
    }

    renderText = () => {
        return (
            <MDBBtnGroup size='lg' className="sidebarButtons">
                <MDBBtn color='danger'>
                    <span style={{ color: '#030C49' }}>Todays Usage</span>
                </MDBBtn>
            </MDBBtnGroup>
        );
    };

    renderValue = () => {
        return (<MDBBtnGroup size='lg' className="sidebarButtons">
            <MDBBtn color='danger'>
                <span style={{ color: '#030C49' }}>51 kWh</span>
            </MDBBtn>
        </MDBBtnGroup>
        );
    };

    changeViewMode = () => {
        if (this.state.viewMode === INCENT_VIEW_MODES.VALUE) this.setState({ viewMode: INCENT_VIEW_MODES.TEXT });
        if (this.state.viewMode === INCENT_VIEW_MODES.TEXT) this.setState({ viewMode: INCENT_VIEW_MODES.VALUE });
    };



    render() {
        return (
            <div onClick={this.changeViewMode}>
                {this.state.viewMode === INCENT_VIEW_MODES.TEXT && this.renderText()}
                {this.state.viewMode === INCENT_VIEW_MODES.VALUE && this.renderValue()}
            </div>
        );
    }

}

export default IncentOnClick;
