import React from 'react';
import Tabs from 'react-bootstrap/Tabs';
import Tab from 'react-bootstrap/Tab';
import Card from 'react-bootstrap/Card';
import ChartsPageDevices from './ChartsPageDevices';
import ListGroup from 'react-bootstrap/ListGroup'
import Toggle from './ToggleSwitch'



function DeviceCard(props) {

    return (

        <div>
            {props.device.map(device => {

                let dataSet_temperature = room.room_data[0].tempurature;
                var dataArray_temperature = dataSet_temperature.split(',', 24);

                let dataSet_humidity = room.room_data[0].humidity;
                var dataArray_humidity = dataSet_humidity.split(',', 24);


                const dataLine = {
                    labels: ['00:00', '01:00', '02:00', '03:00', '04:00', '05:00', '06:00', '07:00', '08:00', '09:00', '10:00', '11:00', '12:00', '13:00', '14:00', '15:00', '16:00', '17:00', '18:00', '19:00', '20:00', '21:00', '22:00', '23:00'],
                    datasets: [
                        {
                            label: 'temperature',
                            fill: false,
                            lineTension: 0.1,
                            backgroundColor: 'rgba(75,192,192,0.4)',
                            borderColor: 'rgba(75,192,192,1)',
                            borderCapStyle: 'butt',
                            borderDash: [],
                            borderDashOffset: 0.0,
                            borderJoinStyle: 'miter',
                            pointBorderColor: 'rgba(75,192,192,1)',
                            pointBackgroundColor: '#fff',
                            pointBorderWidth: 1,
                            pointHoverRadius: 5,
                            pointHoverBackgroundColor: 'rgba(75,192,192,1)',
                            pointHoverBorderColor: 'rgba(220,220,220,1)',
                            pointHoverBorderWidth: 2,
                            pointRadius: 1,
                            pointHitRadius: 10,
                            data: dataArray_temperature
                        }
                    ]
                };

                return (

                    <React.Fragment>

                        <Card style={{ width: '30rem' }}>
                            <Card.Body>
                                <Card.Title>{device.device_name} <Toggle /></Card.Title>
                                <Card.Text>
                                    <Tabs defaultActiveKey="data" transition={false} id="noanim-tab-example">
                                        <Tab eventKey="data" title="Data">
                                            <ChartsPageDevices />
                                        </Tab>
                                        <Tab eventKey="settings" title="Settings">
                                            <ListGroup style={{ paddingTop: '6.5%' }}>
                                                <ListGroup.Item>Disconnect Device</ListGroup.Item>
                                                <ListGroup.Item>Move Rooms</ListGroup.Item>
                                                <ListGroup.Item>Set User Permissions for this device</ListGroup.Item>
                                            </ListGroup>
                                        </Tab>
                                    </Tabs>
                                </Card.Text>
                            </Card.Body>
                        </Card>
                        <br></br>
                    </React.Fragment>
                )
            })}
        </div>

    )
}

export default DeviceCard;
