import React, { Component } from 'react';
import '../index.css';
import Tabs from 'react-bootstrap/Tabs';
import Tab from 'react-bootstrap/Tab';
import RegisterDwelling from './RegisterDwelling';
import { withCookies } from 'react-cookie';
import { MDBContainer, MDBCollapse } from 'mdbreact';

class Login extends Component {

    state = {
        credentials: {
            username: '',
            password: '',
            confirmPassword: '',
            email: '',
            firstname: '',
            lastname: '',
            tel: '',
            address: '',
            homeUsername: '',
            ownerUsername: '',
            collapseID: ''
        },
        isLoginView: true
    }

    toggleCollapse = collapseID => () => {
        this.setState(prevState => ({
            collapseID: prevState.collapseID !== collapseID ? collapseID : ''
        }));
    };


    inputChanged = event => {
        let cred = this.state.credentials;
        cred[event.target.name] = event.target.value;
        this.setState({ credentials: cred });
    }

    login = event => {
        if (this.state.isLoginView) {
            console.log(this.state.credentials);
            fetch('${#', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(this.state.credentials)
            }).then(res => {
                console.log(res.token);
                this.props.cookies.set('token', res.token);
                window.location.href = "/Oko/"
            })
                .catch(error => console.log(error))
        } else {
            console.log(this.state.credentials);
            fetch('${#', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(this.state.credentials)
            }).then(res => {
                this.setState({ isLoginView: true });
            })
                .catch(error => console.log(error))
        }

    }

    render() {
        const { collapseID } = this.state;
        return (

            <React.Fragment>
                <span className="login-fill"><span style={{ color: "red" }}>*</span> Email</span><br />
                <input type="email" name="email" value={this.state.credentials.email} onChange={this.inputChanged} /><br />
                <button onClick={this.login}>
                    Submit
                </button>
            </React.Fragment>
        );
    }
}

export default Login;
